import { useCallback, useEffect, useRef, useState } from "react";
import {
  FaArrowLeft,
  FaCalendarAlt,
  FaCheck,
  FaClock,
  FaEdit,
  FaEnvelope,
  FaFilePdf,
  FaMapMarkerAlt,
  FaPlus,
  FaSave,
  FaSearch,
  FaStar,
  FaTimes,
  FaTrash,
  FaUpload,
} from "react-icons/fa";
import { useNavigate, useParams } from "react-router-dom";
import { supabase } from "../client";
import "../styling/eventDetails.css";

// --- Sub-components (EventSchedule, EventTheme) are unchanged ---
const EventSchedule = ({ schedule, onUpdate, isEditing }) => {
  const handleAddItem = () =>
    onUpdate([...(schedule || []), { time: "", activity: "" }]);
  const handleRemoveItem = (index) =>
    onUpdate((schedule || []).filter((_, i) => i !== index));
  const handleItemChange = (index, field, value) => {
    const newSchedule = [...(schedule || [])];
    newSchedule[index][field] = value;
    onUpdate(newSchedule);
  };
  return (
    <div className="schedule-section">
      <div className="section-header">
        <h2>Schedule</h2>
      </div>
      {isEditing ? (
        <>
          {(schedule || []).map((item, index) => (
            <div key={index} className="schedule-item editable">
              <input
                type="time"
                value={item.time}
                onChange={(e) =>
                  handleItemChange(index, "time", e.target.value)
                }
              />
              <input
                type="text"
                value={item.activity}
                onChange={(e) =>
                  handleItemChange(index, "activity", e.target.value)
                }
                placeholder="Activity"
              />
              <button
                onClick={() => handleRemoveItem(index)}
                className="remove-btn"
              >
                <FaTrash />
              </button>
            </div>
          ))}
          <button onClick={handleAddItem} className="add-btn">
            <FaPlus /> Add Activity
          </button>
        </>
      ) : (
        <div className="schedule-list">
          {schedule && schedule.length > 0 ? (
            schedule.map((item, index) => (
              <div key={index} className="schedule-item">
                <span className="schedule-time">{item.time}</span>
                <span className="schedule-activity">{item.activity}</span>
              </div>
            ))
          ) : (
            <p>No schedule items have been added yet.</p>
          )}
        </div>
      )}
    </div>
  );
};

const EventTheme = ({ theme, onUpdate, isEditing = true }) => {
  const safeTheme =
    typeof theme === "string"
      ? (() => {
          try {
            return JSON.parse(theme);
          } catch (e) {
            return { name: "", colors: [], notes: "" };
          }
        })()
      : theme || { name: "", colors: [], notes: "" };

  const handleChange = (field, value) => {
    onUpdate({ ...safeTheme, [field]: value });
  };

  const handleColorChange = (index, value) => {
    const newColors = [...(safeTheme.colors || [])];
    newColors[index] = value;
    onUpdate({ ...safeTheme, colors: newColors });
  };

  const addColor = () => {
    onUpdate({
      ...safeTheme,
      colors: [...(safeTheme.colors || []), "#ffffff"],
    });
  };

  const removeColor = (index) => {
    onUpdate({
      ...safeTheme,
      colors: (safeTheme.colors || []).filter((_, i) => i !== index),
    });
  };

  return (
    <div className="theme-section">
      <div className="section-header">
        <h2>Event Theme</h2>
      </div>
      {isEditing ? (
        <div className="theme-editor">
          <div className="form-group">
            <label>Theme Name</label>
            <input
              type="text"
              value={safeTheme.name || ""}
              onChange={(e) => handleChange("name", e.target.value)}
              placeholder="Enter theme name"
            />
          </div>

          <div className="form-group">
            <label>Colors</label>
            <div className="color-palette">
              {safeTheme.colors &&
                safeTheme.colors.map((color, index) => (
                  <div key={index} className="color-item">
                    <input
                      type="color"
                      value={color}
                      onChange={(e) => handleColorChange(index, e.target.value)}
                    />
                    <button
                      onClick={() => removeColor(index)}
                      className="remove-color"
                    >
                      <FaTimes />
                    </button>
                  </div>
                ))}
              <button onClick={addColor} className="add-color">
                <FaPlus /> Add Color
              </button>
            </div>
          </div>

          <div className="form-group">
            <label>Theme Notes</label>
            <textarea
              value={safeTheme.notes || ""}
              onChange={(e) => handleChange("notes", e.target.value)}
              placeholder="Add notes about your theme..."
              rows="3"
            />
          </div>
        </div>
      ) : (
        <div className="theme-display">
          {safeTheme.name ? (
            <>
              <h3>{safeTheme.name}</h3>
              {safeTheme.colors && safeTheme.colors.length > 0 && (
                <div className="color-palette-display">
                  {safeTheme.colors.map((color, index) => (
                    <div
                      key={index}
                      className="color-swatch"
                      style={{ backgroundColor: color }}
                      title={color}
                    />
                  ))}
                </div>
              )}
              {safeTheme.notes && <p>{safeTheme.notes}</p>}
            </>
          ) : (
            <p>No theme has been set for this event.</p>
          )}
        </div>
      )}
    </div>
  );
};
const GuestManagement = ({
  guests,
  onUpdate,
  isEditing,
  eventData,
  API_URL,
}) => {
  const [newGuest, setNewGuest] = useState({
    name: "",
    contact: "",
    dietary: "",
    rsvpStatus: "pending",
  });

  const handleAddGuest = () => {
    if (newGuest.name.trim() !== "") {
      onUpdate([...(guests || []), { ...newGuest, id: Date.now() }]);
      setNewGuest({
        name: "",
        contact: "",
        dietary: "",
        rsvpStatus: "pending",
      });
    }
  };

  const handleUpdateGuest = (guestId, field, value) =>
    onUpdate(
      (guests || []).map((g) =>
        g.id === guestId ? { ...g, [field]: value } : g
      )
    );

  const handleRemoveGuest = (guestId) =>
    onUpdate((guests || []).filter((g) => g.id !== guestId));

  const handleSendInvite = async (guest) => {
    alert(
      `Email functionality is temporarily disabled. Guest ${guest.name} (${
        guest.contact
      }) would receive an invitation to ${eventData?.name || "the event"}.`
    );
  };

  return (
    <div className="guests-section">
      <div className="section-header">
        <h2>Guest Management</h2>
      </div>

      {isEditing && (
        <div className="add-guest-form">
          <input
            type="text"
            placeholder="Guest Name"
            value={newGuest.name}
            onChange={(e) => setNewGuest({ ...newGuest, name: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            value={newGuest.contact}
            onChange={(e) =>
              setNewGuest({ ...newGuest, contact: e.target.value })
            }
          />
          <input
            type="text"
            placeholder="Dietary Requirements"
            value={newGuest.dietary}
            onChange={(e) =>
              setNewGuest({ ...newGuest, dietary: e.target.value })
            }
          />
          <button onClick={handleAddGuest}>
            <FaPlus /> Add Guest
          </button>
        </div>
      )}

      <div className="guest-list">
        {guests && guests.length > 0 ? (
          <ul>
            {guests.map((guest) => (
              <li key={guest.id} className="guest-item">
                <div className="guest-info">
                  <div>
                    <strong>{guest.name}</strong>
                    <br />
                    <small>{guest.contact}</small>
                    <br />
                    {guest.dietary && <small>Dietary: {guest.dietary}</small>}
                  </div>
                </div>

                {!isEditing && (
                  <div className="guest-actions view-mode">
                    <button
                      onClick={() => handleSendInvite(guest)}
                      title="Send invite"
                      className="send-invite-btn"
                    >
                      <FaEnvelope /> Send Invite
                    </button>
                    <div className="rsvp-controls">
                      <span>RSVP:</span>
                      <label>
                        <input
                          type="checkbox"
                          checked={guest.rsvpStatus === "attending"}
                          onChange={() => {
                            const newStatus =
                              guest.rsvpStatus === "attending"
                                ? "pending"
                                : "attending";
                            handleUpdateGuest(
                              guest.id,
                              "rsvpStatus",
                              newStatus
                            );
                          }}
                        />{" "}
                        Yes
                      </label>
                      <label>
                        <input
                          type="checkbox"
                          checked={guest.rsvpStatus === "declined"}
                          onChange={() => {
                            const newStatus =
                              guest.rsvpStatus === "declined"
                                ? "pending"
                                : "declined";
                            handleUpdateGuest(
                              guest.id,
                              "rsvpStatus",
                              newStatus
                            );
                          }}
                        />{" "}
                        No
                      </label>
                    </div>
                  </div>
                )}

                {isEditing && (
                  <div className="guest-actions">
                    <button
                      onClick={() => handleRemoveGuest(guest.id)}
                      className="delete-guest"
                      title="Remove guest"
                    >
                      <FaTrash />
                    </button>
                  </div>
                )}
              </li>
            ))}
          </ul>
        ) : (
          <p>No guests have been added yet.</p>
        )}
      </div>
    </div>
  );
};

// Main EventDetails component
const EventDetails = () => {
  const { id } = useParams();
  const searchParams = new URLSearchParams(window.location.search);
  const isReadOnly = searchParams.get("readonly") === "true";
  const eventId = id;
  const navigate = useNavigate();
  // Function to handle vendor card click
  const handleVendorCardClick = (vendorId) => {
    navigate(`/vendors/${vendorId}/services?readonly=true`);
  };
  const [eventData, setEventData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  useEffect(() => {
    if (isReadOnly) {
      setIsEditing(false);
    }
  }, [isReadOnly]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeView, setActiveView] = useState("overview");
  const [formData, setFormData] = useState({
    name: "",
    date: "",
    time: "",
    venue: "",
    notes: "",
  });
  const [vendors, setVendors] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedVendors, setSelectedVendors] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [schedule, setSchedule] = useState([]);
  const [guests, setGuests] = useState([]);
  const initialGuestsRef = useRef([]);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [theme, setTheme] = useState({ name: "", colors: [], notes: "" });

  const API_URL =
    window.location.hostname === "production"
      ? "https://soothing-manifestation-production.up.railway.app"
      : "http://localhost:5000";

  const formatGuestStatusForUI = (dbStatus) => {
    if (dbStatus === "Attending") return "attending";
    if (dbStatus === "Declined") return "declined";
    return "pending";
  };

  const formatGuestStatusForDB = (uiStatus) => {
    if (uiStatus === "attending") return "Attending";
    if (uiStatus === "declined") return "Declined";
    return "Pending";
  };

  const fetchVendors = useCallback(async () => {
    try {
      const {
        data: { session },
      } = await supabase.auth.getSession();
      if (!session) return;

      const response = await fetch(`${API_URL}/api/vendors`, {
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${session.access_token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch vendors");
      }

      const data = await response.json();
      if (Array.isArray(data)) {
        setVendors(data);
      }
    } catch (error) {
      console.error("Error fetching vendors:", error);
    }
  }, [API_URL]);

  useEffect(() => {
    fetchVendors();
  }, [fetchVendors]);

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const {
          data: { user },
        } = await supabase.auth.getUser();
        if (!user) {
          navigate("/login");
          return;
        }

        const eventResponse = await fetch(
          `${API_URL}/api/events/id/${eventId}`
        );
        if (!eventResponse.ok) throw new Error("Failed to fetch event");
        const fetchedEventData = await eventResponse.json();

        if (!fetchedEventData) {
          console.error("Event not found");
          setEventData(null);
          return;
        }

        const guestsResponse = await fetch(`${API_URL}/api/guests/${eventId}`);
        if (!guestsResponse.ok) throw new Error("Failed to fetch guests");
        const guestsData = await guestsResponse.json();

        const formattedGuests = guestsData.map((g) => ({
          id: g.guest_id,
          name: g.name,
          contact: g.email || "",
          dietary: g.dietary_info || "",
          rsvpStatus: formatGuestStatusForUI(g.rsvp_status),
        }));

        setGuests(formattedGuests);
        initialGuestsRef.current = formattedGuests;

        setEventData(fetchedEventData);
        setSchedule(fetchedEventData.schedule || []);
        let themeData = fetchedEventData.theme;

        // If theme is a string, parse it
        if (typeof themeData === "string") {
          try {
            themeData = JSON.parse(themeData);
          } catch (e) {
            console.error("Error parsing theme string:", e);
            themeData = { name: "", colors: [], notes: "" };
          }
        }

        // If theme is an object but corrupted, clean it
        if (themeData && typeof themeData === "object") {
          const hasCorruptedProperties = Object.keys(themeData).some(
            (key) => !isNaN(parseInt(key)) && themeData[key] !== undefined
          );

          if (hasCorruptedProperties) {
            themeData = {
              name: themeData.name || "",
              colors: Array.isArray(themeData.colors) ? themeData.colors : [],
              notes: themeData.notes || "",
            };
          }
        }
        setTheme(themeData || { name: "", colors: [], notes: "" });
        setDocuments(fetchedEventData.documents || []);
        setSelectedVendors(fetchedEventData.vendors || []);

        const startTime = new Date(fetchedEventData.start_time);
        const date = startTime.toISOString().split("T")[0];
        const time = startTime.toTimeString().substring(0, 5);

        setFormData({
          name: fetchedEventData.name || "",
          date,
          time,
          venue: fetchedEventData.venue || "",
          notes: fetchedEventData.notes || "",
        });
      } catch (error) {
        console.error("Error loading data:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [eventId, navigate, API_URL]);

  const API_BASE = process.env.REACT_APP_BASE_URL || "http://localhost:5000";
  const [vendorRequests, setVendorRequests] = useState([]);

  useEffect(() => {
    const fetchVendorRequests = async () => {
      try {
        if (!eventId) return;

        const response = await fetch(
          `${API_BASE}/api/vendor-requests/event/${eventId}`
        );

        if (response.ok) {
          const requestsData = await response.json();
          setVendorRequests(requestsData || []);
        }
      } catch (error) {
        console.error("Error fetching vendor requests:", error);
      }
    };

    fetchVendorRequests();
  }, [eventId, API_BASE]);

  const handleVendorRequestResponse = async (requestId, status) => {
    try {
      const response = await fetch(
        `${API_BASE}/api/vendor-requests/${requestId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ status }),
        }
      );

      if (response.ok) {
        setVendorRequests((prev) =>
          prev.map((req) =>
            req.request_id === requestId ? { ...req, status } : req
          )
        );

        if (status === "accepted") {
          const request = vendorRequests.find(
            (req) => req.request_id === requestId
          );
          if (request && request.vendor_id) {
            setSelectedVendors((prev) => [...prev, request.vendor_id]);
          }
        }

        alert(`Vendor request ${status} successfully!`);
      }
    } catch (error) {
      console.error("Error updating vendor request:", error);
      alert("Failed to update vendor request.");
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleGuestUpdates = async () => {
    const currentGuestsMap = new Map(guests.map((g) => [g.id, g]));
    const promises = [];

    for (const initialGuest of initialGuestsRef.current) {
      if (!currentGuestsMap.has(initialGuest.id)) {
        promises.push(
          fetch(`${API_URL}/api/guests/${eventId}/${initialGuest.id}`, {
            method: "DELETE",
          })
        );
      }
    }

    for (const currentGuest of guests) {
      const payload = {
        name: currentGuest.name,
        email: currentGuest.contact,
        rsvp_status: formatGuestStatusForDB(currentGuest.rsvpStatus),
        dietary_info: currentGuest.dietary,
      };

      if (typeof currentGuest.id === "number") {
        promises.push(
          fetch(`${API_URL}/api/guests/${eventId}`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
          })
        );
      } else {
        const initialGuest = initialGuestsRef.current.find(
          (g) => g.id === currentGuest.id
        );
        if (JSON.stringify(initialGuest) !== JSON.stringify(currentGuest)) {
          promises.push(
            fetch(`${API_URL}/api/guests/${eventId}/${currentGuest.id}`, {
              method: "PUT",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify(payload),
            })
          );
        }
      }
    }

    await Promise.all(promises);
  };

  const cleanThemeObject = (theme) => {
    if (!theme || typeof theme !== "object") {
      return { name: "", colors: [], notes: "" };
    }

    const hasCorruptedProperties = Object.keys(theme).some(
      (key) => !isNaN(parseInt(key)) && theme[key] !== undefined
    );

    if (hasCorruptedProperties) {
      // Reconstruct a clean theme object
      return {
        name: theme.name || "",
        colors: Array.isArray(theme.colors) ? theme.colors : [],
        notes: theme.notes || "",
      };
    }

    return {
      name: theme.name || "",
      colors: Array.isArray(theme.colors) ? theme.colors : [],
      notes: theme.notes || "",
    };
  };

  const handleSave = async () => {
    if (isReadOnly) return;
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) return;

      await handleGuestUpdates();

      const cleanTheme = cleanThemeObject(theme);

      const startTime = formData.time
        ? `${formData.date}T${formData.time}`
        : `${formData.date}T00:00:00`;

      const mainEventPayload = {
        name: formData.name,
        venue: formData.venue,
        theme: cleanTheme,
        start_time: `${formData.date}T${formData.time || "00:00"}:00`,
      };

      const response = await fetch(`${API_URL}/api/events/${eventId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(mainEventPayload),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to update event");
      }

      const result = await response.json();
      console.log("Save response:", result);

      setIsEditing(false);

      const guestsResponse = await fetch(`${API_URL}/api/guests/${eventId}`);
      const guestsData = await guestsResponse.json();
      const formattedGuests = guestsData.map((g) => ({
        id: g.guest_id,
        name: g.name,
        contact: g.email || "",
        dietary: g.dietary_info || "",
        rsvpStatus: formatGuestStatusForUI(g.rsvp_status),
      }));
      setGuests(formattedGuests);
      initialGuestsRef.current = formattedGuests;

      setEventData((prev) => ({
        ...prev,
        name: formData.name,
        start_time: startTime,
        venue: formData.venue,
        theme: theme,
      }));

      alert("Event and guests updated successfully!");
    } catch (error) {
      console.error("Error updating event:", error);
      alert(`Failed to update event: ${error.message}`);
    }
  };

  const handleVendorToggle = (vendorId) => {
    if (isReadOnly) return;
    setSelectedVendors((prev) =>
      prev.includes(vendorId)
        ? prev.filter((id) => id !== vendorId)
        : [...prev, vendorId]
    );
  };

  const handleExport = async () => {
    try {
      const response = await fetch(`${API_URL}/api/events/${eventId}/export`, {
        method: "GET",
      });
      if (!response.ok) throw new Error("Failed to export event data");

      const disposition = response.headers.get("Content-Disposition");
      let filename = `event_export_${eventId}.zip`;
      if (disposition && disposition.includes("filename=")) {
        filename = disposition.split("filename=")[1].replace(/"/g, "");
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      alert("Export failed: " + error.message);
    }
  };

  const handleFileUpload = async (e) => {
    if (isReadOnly) return;
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setIsUploading(true);
    setUploadProgress(0);
    const newDocs = [];
    const totalFiles = files.length;

    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) return;

      for (let i = 0; i < totalFiles; i++) {
        const file = files[i];
        const safeEventName = formData.name
          .replace(/\s+/g, "_")
          .replace(/[^\w-]/g, "");
        const filePath = `events/${eventId}/${safeEventName}/${file.name}`;

        const { error: uploadError } = await supabase.storage
          .from("event-documents")
          .upload(filePath, file);

        if (uploadError) throw uploadError;

        const {
          data: { publicUrl },
        } = supabase.storage.from("event-documents").getPublicUrl(filePath);

        newDocs.push({
          name: file.name,
          url: publicUrl,
          uploaded_at: new Date().toISOString(),
        });
        setUploadProgress(Math.round(((i + 1) / totalFiles) * 100));
      }

      if (newDocs.length > 0) {
        const { error } = await supabase
          .from("events")
          .update({
            documents: [...documents, ...newDocs],
            updated_at: new Date().toISOString(),
          })
          .eq("event_id", eventId);

        if (error) throw error;

        setDocuments((prev) => [...prev, ...newDocs]);
      }
    } catch (error) {
      console.error("Error uploading files:", error);
      alert(`An error occurred during upload: ${error.message}`);
    } finally {
      setIsUploading(false);
      e.target.value = null;
    }
  };

  const handleDeleteDocument = async (docToDelete) => {
    if (!window.confirm("Are you sure you want to delete this document?"))
      return;
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) return;

      const { error } = await supabase
        .from("events")
        .update({
          documents: documents.filter((doc) => doc.url !== docToDelete.url),
          updated_at: new Date().toISOString(),
        })
        .eq("event_id", eventId);

      if (error) throw error;

      setDocuments((prev) => prev.filter((doc) => doc.url !== docToDelete.url));
    } catch (error) {
      console.error("Error deleting document reference:", error);
    }
  };

  if (isLoading) return <div className="loading">Loading event details...</div>;
  if (!eventData)
    return (
      <div className="error">
        <h2>Event Not Found</h2>
        <p>
          The requested event could not be found or you don't have permission to
          view it.
        </p>
        <button onClick={() => navigate("/dashboard")}>
          Back to Dashboard
        </button>
      </div>
    );

  const formatDisplayDate = (dateString) => {
    if (!dateString) return "Not specified";
    const date = new Date(dateString + "T00:00:00");
    return date.toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const toggleEdit = () => {
    if (!isReadOnly) {
      setIsEditing(!isEditing);
    }
  };

  return (
    <div className="event-details">
      <div className="event-header">
        <button onClick={() => navigate(-1)} className="back-button">
          <FaArrowLeft /> Back to Dashboard
        </button>
        <div className="button-group">
          <button
            onClick={() => setActiveView("overview")}
            className={`new-button ${
              activeView === "overview" ? "active" : ""
            }`}
          >
            Event Overview
          </button>
          <button
            onClick={() => setActiveView("guests")}
            className={`new-button ${activeView === "guests" ? "active" : ""}`}
          >
            Guest Management
          </button>
          <button
            onClick={() => setActiveView("vendors")}
            className={`new-button ${activeView === "vendors" ? "active" : ""}`}
          >
            Vendor Management
          </button>
          <button
            onClick={() => setActiveView("documents")}
            className={`new-button ${
              activeView === "documents" ? "active" : ""
            }`}
          >
            Document Management
          </button>
        </div>
        <div className="header-actions">
          {isEditing ? (
            <button onClick={handleSave} className="save-button">
              <FaSave /> Save Changes
            </button>
          ) : (
            !isReadOnly && (
              <button onClick={toggleEdit} className="edit-button">
                <FaEdit /> Edit Event
              </button>
            )
          )}
          <button onClick={handleExport} className="export-button">
            <FaUpload /> Export Event Data
          </button>
        </div>
      </div>
      <div className="event-info-boxes">
        <div className="info-box date-box">
          <h4>
            <FaCalendarAlt /> Date
          </h4>
          <p>
            {isEditing ? (
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleInputChange}
              />
            ) : (
              formatDisplayDate(formData.date)
            )}
          </p>
        </div>
        <div className="info-box time-box">
          <h4>
            <FaClock /> Time
          </h4>
          <p>
            {isEditing ? (
              <input
                type="time"
                name="time"
                value={formData.time}
                onChange={handleInputChange}
              />
            ) : (
              formData.time || "Not specified"
            )}
          </p>
        </div>
        <div className="info-box venue-box">
          <h4>
            <FaMapMarkerAlt /> Venue
          </h4>
          <p>
            {isEditing ? (
              <input
                type="text"
                name="venue"
                value={formData.venue}
                onChange={handleInputChange}
                placeholder="Venue"
              />
            ) : (
              eventData.venue || "Not specified"
            )}
          </p>
        </div>
        <div className="info-box theme-box">
          <h4>
            <FaStar /> Theme
          </h4>
          <p>{eventData.theme?.name || "Not specified"}</p>
        </div>
      </div>
      <div className="event-sections">
        {activeView === "overview" && (
          <>
            <section>
              <EventSchedule
                schedule={schedule}
                onUpdate={setSchedule}
                isEditing={isEditing}
              />
            </section>
            <section>
              <EventTheme
                theme={theme}
                onUpdate={setTheme}
                isEditing={isEditing}
              />
            </section>
          </>
        )}
        {activeView === "guests" && (
          <GuestManagement
            guests={guests}
            onUpdate={setGuests}
            isEditing={isEditing}
            eventData={eventData}
            API_URL={API_URL}
          />
        )}
        {activeView === "vendors" && (
          <section className="vendors-section">
            <div className="section-header">
              <h2>Vendors</h2>
            </div>
            {isEditing ? (
              <div>
                <div className="vendor-search-container">
                  <div className="search-box">
                    <FaSearch className="search-icon" />
                    <input
                      type="text"
                      placeholder="Search for vendors by name or category"
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="search-input"
                    />
                    {searchTerm && (
                      <button
                        type="button"
                        onClick={() => setSearchTerm("")}
                        className="clear-search"
                        aria-label="Clear search"
                      >
                        <FaTimes />
                      </button>
                    )}
                  </div>
                  <select
                    className="category-filter"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                  >
                    <option value="All">All Categories</option>
                    {Array.from(
                      new Set(
                        [
                          // This gets all categories from your vendor data
                          ...vendors.map((v) =>
                            v.service_type.trim().toLowerCase()
                          ),
                          // This line ensures 'photography' is always in the list
                          "photography",
                        ].filter(Boolean)
                      )
                    )
                      .sort()
                      .map((category) => (
                        <option key={category} value={category}>
                          {category.charAt(0).toUpperCase() + category.slice(1)}
                        </option>
                      ))}
                  </select>
                </div>
                <div className="vendor-selection">
                  {vendors
                    .filter((vendor) => {
                      const matchesSearch =
                        !searchTerm ||
                        vendor.business_name
                          .toLowerCase()
                          .includes(searchTerm.toLowerCase()) ||
                        vendor.service_type
                          .toLowerCase()
                          .includes(searchTerm.toLowerCase());
                      const matchesCategory =
                        selectedCategory === "All" ||
                        vendor.service_type.trim().toLowerCase() ===
                          selectedCategory;
                      return matchesSearch && matchesCategory;
                    })
                    .map((vendor) => (
                      <div
                        key={vendor.vendor_id}
                        className="vendor-card"
                        onClick={(e) => {
                          // Only navigate if the click is not on the request button or its children
                          if (!e.target.closest('.add-vendor-btn, .undo-request-btn')) {
                            handleVendorCardClick(vendor.vendor_id);
                          }
                        }}
                        style={{ cursor: "pointer" }}
                      >
                        <div className="vendor-card-content">
                          <div className="vendor-info">
                            <h4>{vendor.business_name}</h4>
                          </div>
                          <span className="vendor-category">
                            {vendor.service_type.toLowerCase() === "venue"
                              ? "Venue"
                              : vendor.service_type}
                          </span>
                          <div className="vendor-description" style={{ textAlign: 'center' }}>
                            {vendor.description || "No description available."}
                          </div>
                        </div>
                        <div className="vendor-actions">
                          <button
                            type="button"
                            className={`add-vendor-btn ${
                              selectedVendors.includes(vendor.vendor_id)
                                ? "added"
                                : ""
                            }`}
                            onClick={() => handleVendorToggle(vendor.vendor_id)}
                            disabled={!isEditing}
                          >
                            {selectedVendors.includes(vendor.vendor_id) ? (
                              <>
                                <FaCheck /> Requested
                              </>
                            ) : (
                              <>
                                <FaPlus /> Request Vendor
                              </>
                            )}
                          </button>
                          {selectedVendors.includes(vendor.vendor_id) && (
                            <button
                              type="button"
                              className="undo-request-btn"
                              onClick={() =>
                                handleVendorToggle(vendor.vendor_id)
                              }
                              disabled={!isEditing}
                            >
                              <FaTimes /> Undo
                            </button>
                          )}
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            ) : (
              <div className="vendors-list">
                {selectedVendors.length > 0 ? (
                  <ul>
                    {selectedVendors.map((vendorId) => {
                      const vendor = vendors.find(
                        (v) => v.vendor_id === vendorId
                      );
                      return vendor ? (
                        <li key={vendorId}>
                          <strong>{vendor.business_name}</strong> -{" "}
                          {vendor.service_type}
                          <br />
                          <small>{vendor.contact_number}</small>
                        </li>
                      ) : null;
                    })}
                  </ul>
                ) : (
                  <div className="no-vendors-section">
                    <p>No vendors selected</p>
                    {vendorRequests && vendorRequests.length > 0 && (
                      <div className="vendor-requests-container">
                        <h4>Vendor Requests</h4>
                        <div className="vendor-requests-list">
                          {vendorRequests.map((request) => (
                            <div
                              key={request.request_id}
                              className="vendor-request-item"
                            >
                              <div className="vendor-request-info">
                                <strong>
                                  {request.vendor?.business_name || "Vendor"}
                                </strong>
                                <span
                                  className={`status-badge status-${request.status}`}
                                >
                                  {request.status}
                                </span>
                                <div className="vendor-details">
                                  <small>
                                    Service:{" "}
                                    {request.vendor?.service_type || "Unknown"}
                                  </small>
                                  <small>
                                    Contact:{" "}
                                    {request.vendor?.contact_number ||
                                      "Not provided"}
                                  </small>
                                </div>
                              </div>
                              <div className="vendor-request-actions">
                                {request.status === "pending" && isEditing && (
                                  <div className="action-buttons">
                                    <button
                                      onClick={() =>
                                        handleVendorRequestResponse(
                                          request.request_id,
                                          "accepted"
                                        )
                                      }
                                      className="accept-btn"
                                    >
                                      Accept
                                    </button>
                                    <button
                                      onClick={() =>
                                        handleVendorRequestResponse(
                                          request.request_id,
                                          "rejected"
                                        )
                                      }
                                      className="reject-btn"
                                    >
                                      Reject
                                    </button>
                                  </div>
                                )}
                                {request.status === "accepted" && (
                                  <span className="status-label accepted">
                                     Accepted
                                  </span>
                                )}
                                {request.status === "rejected" && (
                                  <span className="status-label rejected">
                                     Rejected
                                  </span>
                                )}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}
          </section>
        )}
        {activeView === "documents" && (
          <section className="documents-section">
            <div className="documents-header">
              <h2>Documents</h2>
            </div>
            {isEditing && (
              <div className="upload-area">
                <label className="upload-button">
                  <FaUpload /> Upload Documents
                  <input
                    type="file"
                    multiple
                    onChange={handleFileUpload}
                    style={{ display: "none" }}
                    disabled={isUploading}
                  />
                </label>
                {isUploading && (
                  <div className="upload-progress">
                    <progress value={uploadProgress} max="100" />
                    <span>Uploading... {uploadProgress}%</span>
                  </div>
                )}
              </div>
            )}
            <div className="documents-list">
              {documents && documents.length > 0 ? (
                <ul>
                  {documents.map((doc, index) => (
                    <li key={index} className="document-item">
                      <a
                        href={doc.url}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        <FaFilePdf /> {doc.name}
                      </a>
                      {isEditing && (
                        <button
                          onClick={() => handleDeleteDocument(doc)}
                          className="delete-doc"
                          title="Delete document"
                        >
                          <FaTimes />
                        </button>
                      )}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No documents uploaded yet</p>
              )}
            </div>
          </section>
        )}
      </div>
    </div>
  );
};

export default EventDetails;
